import * as React from 'react'
import { useState, useEffect, useRef } from 'react'
import Conversations from './Conversations'
import LoginForm from './LoginForm'

/**
 * Componente raíz para la página.
 * Aquí se renderizarán tanto el inicio de sesión como el chat.
 */

export const UserContext = React.createContext(undefined);

function MainPage (props: any) {
    // Estado de autenticación
    const [userAuth, setUserAuth] = useState('false')
    // Usuario
    const [user, setUser] = useState();
    // Referencia del JSX a renderizar
    const canvas = useRef(
        <div className="Login-page">
            <LoginForm authUpdater={setUserAuth} userSetter={setUser} />
        </div>
    )
    // Contexto para el usuario

    // Función para actualizar el componente que se va a mostrar si 
    // cambia el valor de userAuth.
    function updateCanvas()  {
        if (userAuth) {
            canvas.current = (
                <UserContext.Provider value={user}>
                    <Conversations />
                </UserContext.Provider>
            )
        } else {
            canvas.current = (
                <div className="Login-page">
                    <LoginForm authUpdater={setUserAuth} userSetter={setUser} />
                </div>
            )
        }
    }

    // Si cambia userAuth se llama a updateCanvas
    useEffect(updateCanvas, [userAuth])

    return canvas.current
}

export default MainPage
