import * as React from 'react'
import { useState, useEffect, useRef } from 'react'
import Conversations from './Conversations'
import LoginForm from './LoginForm'

/**
 * Componente raíz para la página.
 * Aquí se renderizarán tanto el inicio de sesión como el chat.
 */
function MainPage (props: any) {
    // Estado de autenticación
    const [userAuth, setUserAuth] = useState(false)
    const [userName, setUserName] = useState();
    // Referencia del JSX a renderizar
    const canvas = useRef(
        <div className="Login-page">
            <LoginForm authUpdater={setUserAuth} userSetter={setUserName} />
        </div>
    )
    // Contexto para el usuario

    // Función para actualizar el componente que se va a mostrar si 
    // cambia el valor de userAuth.

    if (userAuth) {
        return <Conversations user={userName}/>
    }

    return (
        <div className="Login-page">
            <LoginForm authUpdater={setUserAuth} userSetter={setUserName} />
        </div>
    )
}

export default MainPage
