import * as React from "react";
import { useState } from "react";

/**
 * Envía los mensajes a ChatPanel.
 * @param props
 * @returns
 */
export default function MessageSender(props: any) {
  const [msg, setMsg] = useState({});

  function handleMsgChange(event: any) {
    setMsg({ sender: "user", text: event.trigger.value });
  }

  function handleSubmit(event: any) {
    props.addMessage((prevMsgs: any) => [...prevMsgs, msg]);
  }

  return (
    <div className="Message-sender">
      <form onSubmit={handleSubmit}>
        <input
          className="msg-box"
          onChange={handleMsgChange}
          value={msg}
          type="text"
        />
        <input className="send-button" type="submit" value="&nbsp;>&nbsp;" />
      </form>
    </div>
  );
}
