import * as React from 'react'
import { useRef } from 'react';

/**
 * Muestra la lista de amigos.
 * @param props 
 * @returns 
 */

export default function FriendList(props: any) {
    const canvas = useRef(<></>)

    const drawFriendList = async () => {
        canvas.current = (<ol>
        {
            props.friends.map((friend: {name: string; status: string}) => (
                <li>{friend.name} : {friend.status}</li>
                )
        )}
        </ol>)
    }

    drawFriendList()
    .then(() => {return canvas.current}) 
    .catch(() => {return (<div>An error ocurred</div>)})
}
