import * as React from "react";

export default function MessageHolder(props: any) {
  const msgs: Array<JSX.Element> = [];

  // Por cada mensaje que haya se crea una ROW de la tabla de mensajes.
  // La variables messages contiene todas estas rows
  props.messages.map((msg: any) => {
    if (msg.sender === props.user) {
      msgs.push(
        <tr>
          <td></td>
          <td>{msg.text}</td>
        </tr>
      );
    } else {
      msgs.push(
        <tr>
          <td>{msg.text}</td>
          <td></td>
        </tr>
      );
    }
  });

  return (
    <table className="Message-holder">
      <tr>
        <th>Nombre de amigo</th>
        {/* <th>{props.friend.name}</th> */}
      </tr>
      {msgs}
    </table>
  );
}
