import * as React from "react";
import { useState } from "react";
import MessageHolder from "./MessageHolder";
import MessageSender from "./MessageSender";

export default function ChatPanel(props: any) {
  const [messages, setMessages] = useState([
    { sender: props.user, text: "Haz click en algún amigo conectado" },
    { sender: props.user, text: "Y comienza a hablar!" },
  ]);

  React.useEffect(() => {
    setMessages([{ sender: "", text: "" }]);
  }, [props.friend]);

  return (
    <div className="Chat-panel">
      <MessageHolder
        user={props.user}
        friend={props.friend}
        messages={messages}
      />
      <MessageSender />
    </div>
  );
}
