import * as React from 'react'
import { useRef, useState } from 'react';

/**
 * Muestra la lista de amigos.
 * @param props 
 * @returns 
 */

export default function FriendList(props: any) {
    const canvas = useRef(<></>)
    const [userName, setuserName] = useState(props.user);

    const drawFriendList = async () => {
        if (props.user === undefined) {
            canvas.current = (<div>User empty</div>)
        } else {
            canvas.current = (
            <ol>{
                    props.user.friends.map((friend: {name: string; status: string}) => (
                        <li>{friend.name} : {friend.status}</li>
                        )
                )}
            </ol>)
        }

    }

    drawFriendList()
    .catch(() => {canvas.current = (<div>An error ocurred</div>)})

    // return canvas.current
    return <div>User name is {userName}</div>
}
